$(document).ready(function(){var $btn=$("#filter .button");$btn.click(function(){if(this.id=="all"){$("#portfolio > div").fadeIn()}else{var $el=$("."+this.id);$el.fadeIn();$("#portfolio > div").not($el).hide()}
$btn.removeClass("is-warning");$(this).addClass("is-warning")})});document.addEventListener("DOMContentLoaded",function(){let moveDistance=0.8*window.innerWidth;let slightVerticalShift=0.1*window.innerHeight;let largerVerticalShiftForRed=0.25*window.innerHeight;let largerVerticalShiftForPink=0.15*window.innerHeight;let greenBlockAdjustment=0;if(window.innerWidth<=1300){greenBlockAdjustment=0.2*moveDistance}
const rectangles={rect1:{x:-moveDistance,y:0},rect2:{x:-0.5*moveDistance+greenBlockAdjustment,y:-slightVerticalShift},rect3:{x:-0.9*moveDistance,y:largerVerticalShiftForRed},rect4:{x:0.7*moveDistance,y:-largerVerticalShiftForPink},rect5:{x:moveDistance,y:0}};let svgElement=document.querySelector('.expanding-svg');let animationFinished=!1;let hasScrolled=!1;function animateBlocks(endX,endY,rectId){const rect=document.getElementById(rectId);rect.animate([{transform:getComputedStyle(rect).transform},{transform:`translate(${endX}px, ${endY}px)`}],{duration:1000,easing:'ease-out',fill:'forwards'})}
setTimeout(function(){for(const[id,{x,y}]of Object.entries(rectangles)){animateBlocks(x,y,id)}
setTimeout(function(){document.getElementById('centerImageLink').style.opacity="1"},1000)},1000);window.addEventListener('scroll',function(){if(!animationFinished)return;if(window.scrollY===0){hasScrolled=!1;for(const[id,{x,y}]of Object.entries(rectangles)){animateBlocks(x,y,id)}}else if(!hasScrolled){hasScrolled=!0;for(const[id,{x,y}]of Object.entries(rectangles)){animateBlocks(4*x,4*y,id)}}});setTimeout(function(){animationFinished=!0},2000)})